import React from 'react';
import Card from '../../components/Card/Card';
import './Home.css';

const Home = () => {
  const popularSports = [
    { 
      name: "Basketball",
      description: "Develops teamwork, agility, and strategic thinking. Great for improving cardiovascular fitness and hand-eye coordination."
    },
    { 
      name: "Cricket",
      description: "Enhances concentration, physical endurance, and decision-making skills. Perfect blend of individual skill and team strategy."
    },
    { 
      name: "Football",
      description: "Builds stamina, teamwork, and leadership. Excellent for developing lower body strength and cardiovascular health."
    },
    { 
      name: "Volleyball",
      description: "Improves reflexes, coordination, and team communication. Great for building upper body strength and agility."
    },
    { 
      name: "Athletics",
      description: "Develops speed, strength, and endurance. Foundation for all sports and essential for overall fitness development."
    },
    { 
      name: "Badminton",
      description: "Enhances reflexes, footwork, and strategic thinking. Excellent for improving cardiovascular fitness and hand-eye coordination."
    },
    { 
      name: "Table Tennis",
      description: "Sharpens focus, reflexes, and hand-eye coordination. Perfect for developing quick decision-making skills."
    },
    { 
      name: "Swimming",
      description: "Full-body workout that improves endurance and flexibility. Great for cardiovascular health and muscle development."
    },
    { 
      name: "Chess",
      description: "Enhances strategic thinking, problem-solving, and concentration. Develops mental strength and decision-making abilities."
    },
    { 
      name: "Carrom",
      description: "Improves concentration, precision, and strategic planning. Develops fine motor skills and analytical thinking."
    },
    { 
      name: "Kabaddi",
      description: "Builds strength, agility, and raid strategies. Excellent for developing quick thinking and physical stamina."
    },
    { 
      name: "Tennis",
      description: "Enhances agility, hand-eye coordination, and tactical thinking. Great for overall fitness and mental focus."
    }
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>KL Sports Hub</h1>
        <p>Empowering athletes, fostering competition, building champions</p>
      </div>

      <section className="popular-sports">
        <h2>Popular Sports</h2>
        <div className="sports-grid">
          {popularSports.map((sport, index) => (
            <Card key={index}>
              <div className="sport-card">
                <div className="sport-info">
                  <span className="sport-name">{sport.name}</span>
                  <p className="sport-description">{sport.description}</p>
                </div>
              </div>
            </Card>
          ))}
        </div>
      </section>

      <section className="featured-events">
        <h2>Upcoming Events</h2>
        <div className="events-grid">
          {featuredEvents.map(event => (
            <Card key={event.id}>
              <div className="event-card">
                <h3>{event.title}</h3>
                <p className="event-teams">{event.teams}</p>
                <div className="event-details">
                  <span>{event.date}</span>
                  <span>{event.time}</span>
                </div>
                <p className="event-venue">{event.venue}</p>
              </div>
            </Card>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
