import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();

  // Check if current path is login or register related
  const isAuthPage = ['/login', '/register/student', '/register/coach'].includes(location.pathname);

  const handleLogout = () => {
    logout();
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to={user ? "/home" : "/"} className="navbar-logo">
          KL SPORTS HUB
        </Link>

        <div className="nav-links">
          {user ? (
            // Show these links when user is logged in
            <>
              <Link to="/home" className="nav-link">Home</Link>
              <Link to="/tournaments" className="nav-link">Tournaments</Link>
              <Link to="/live-events" className="nav-link">Live Events</Link>
              <button onClick={handleLogout} className="nav-link logout-btn">
                Logout
              </button>
            </>
          ) : (
            // Show nothing when on auth pages
            !isAuthPage && (
              <>
                <Link to="/login" className="nav-link">Login</Link>
                <Link to="/register/student" className="nav-link">Register</Link>
              </>
            )
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
