import React, { useState } from 'react';
import './LiveMatches.css';

const LiveMatches = () => {
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [chatMessages, setChatMessages] = useState([]);
  const [messageInput, setMessageInput] = useState('');

  const liveMatches = [
    {
      id: 1,
      sport: 'Cricket',
      teams: {
        team1: { name: 'CSE Warriors', score: '156/4' },
        team2: { name: 'AIDS Strikers', score: '142/6' }
      },
      status: 'Live',
      overs: '18.4/20',
      venue: 'KLU Main Ground',
      streamUrl: 'https://www.youtube.com/watch?v=8ux5XkIqcRk'
    },
    {
      id: 2,
      sport: 'Basketball',
      teams: {
        team1: { name: 'IOT Dragons', score: '78' },
        team2: { name: 'CSIT Phoenix', score: '82' }
      },
      status: 'Live',
      time: 'Q4 - 2:45',
      venue: 'Indoor Court 1',
      streamUrl: 'https://www.youtube.com/embed/live_stream?channel=YOUR_CHANNEL_ID'
    },
    {
      id: 3,
      sport: 'Football',
      teams: {
        team1: { name: 'EEE United', score: '2' },
        team2: { name: 'CSE Warriors', score: '1' }
      },
      status: 'Live',
      time: '75\'',
      venue: 'KLU Football Ground',
      streamUrl: 'https://www.youtube.com/embed/live_stream?channel=YOUR_CHANNEL_ID'
    }
  ];

  const handleSendMessage = (e) => {
    e.preventDefault();
    if (!messageInput.trim()) return;

    const newMessage = {
      id: Date.now(),
      user: 'User Name', // Replace with actual user name
      content: messageInput,
      timestamp: new Date().toLocaleTimeString()
    };

    setChatMessages([...chatMessages, newMessage]);
    setMessageInput('');
  };

  const handleWatchLive = (match) => {
    setSelectedMatch(match);
  };

  return (
    <div className="live-matches-container">
      <h1 className="section-title">Live Matches</h1>
      
      <div className="live-content">
        <div className="matches-grid">
          {liveMatches.map(match => (
            <div key={match.id} className="match-card">
              <div className="match-header">
                <span className="sport-tag">{match.sport}</span>
                <span className="live-indicator">LIVE</span>
              </div>

              <div className="match-content">
                <div className="team team1">
                  <h3>{match.teams.team1.name}</h3>
                  <span className="score">{match.teams.team1.score}</span>
                </div>

                <div className="match-info">
                  <span className="vs">VS</span>
                  <span className="match-time">
                    {match.overs || match.time}
                  </span>
                </div>

                <div className="team team2">
                  <h3>{match.teams.team2.name}</h3>
                  <span className="score">{match.teams.team2.score}</span>
                </div>
              </div>

              <div className="match-footer">
                <span className="venue">
                  <i className="fas fa-map-marker-alt"></i> {match.venue}
                </span>
                <button 
                  className="btn-watch"
                  onClick={() => handleWatchLive(match)}
                >
                  Watch Live
                </button>
              </div>
            </div>
          ))}
        </div>

        {selectedMatch && (
          <div className="live-stream-section">
            <div className="stream-container">
              <iframe
                src={selectedMatch.streamUrl}
                title="Live Stream"
                frameBorder="0"
                allowFullScreen
              ></iframe>
            </div>

            <div className="live-chat">
              <div className="chat-header">
                <h3>Live Chat</h3>
                <p>{selectedMatch.teams.team1.name} vs {selectedMatch.teams.team2.name}</p>
              </div>

              <div className="chat-messages">
                {chatMessages.map(message => (
                  <div key={message.id} className="chat-message">
                    <div className="message-header">
                      <span className="user-name">{message.user}</span>
                      <span className="timestamp">{message.timestamp}</span>
                    </div>
                    <div className="message-content">{message.content}</div>
                  </div>
                ))}
              </div>

              <form onSubmit={handleSendMessage} className="chat-input">
                <input
                  type="text"
                  value={messageInput}
                  onChange={(e) => setMessageInput(e.target.value)}
                  placeholder="Type your message..."
                />
                <button type="submit">
                  <i className="fas fa-paper-plane"></i>
                </button>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default LiveMatches;
