import React from 'react';
import Card from '../../components/Card/Card';
import './Home.css';

const Home = () => {
  const featuredEvents = [
    {
      id: 1,
      title: "Inter-University Basketball Championship",
      date: "2024-03-20",
      time: "16:00",
      teams: "Engineering vs Medical",
      venue: "University Main Stadium"
    },
    {
      id: 2,
      title: "Annual Athletics Meet",
      date: "2024-03-25",
      time: "09:00",
      teams: "All Departments",
      venue: "University Sports Complex"
    },
    {
      id: 3,
      title: "Inter-Department Cricket Tournament",
      date: "2024-03-28",
      time: "14:30",
      teams: "Science vs Arts",
      venue: "University Cricket Ground"
    }
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>University Sports Management</h1>
        <p>Empowering student athletes, fostering competition, building champions</p>
      </div>

      <section className="featured-events">
        <h2>Upcoming Events</h2>
        <div className="events-grid">
          {featuredEvents.map(event => (
            <Card key={event.id}>
              <div className="event-card">
                <h3>{event.title}</h3>
                <p className="event-teams">{event.teams}</p>
                <div className="event-details">
                  <span>{event.date}</span>
                  <span>{event.time}</span>
                </div>
                <p className="event-venue">{event.venue}</p>
              </div>
            </Card>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
