import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './context/AuthContext';
import ProtectedRoute from './components/ProtectedRoute';
import Navbar from './components/Navbar/Navbar';
import Welcome from './pages/Welcome/Welcome';
import Home from './pages/Home/Home';
import Login from './pages/Auth/Login';
import Register from './pages/Auth/Register';
import LiveEvents from './pages/Events/LiveEvents';
import PlayerStats from './pages/Stats/PlayerStats';
import Departments from './pages/Departments/Departments';
import Profile from './pages/Account/Profile';
import Tournaments from './pages/Tournaments/Tournaments';
import Chatbot from './components/Chatbot/Chatbot';
import Settings from './pages/Account/Settings';
import MyRegistrations from './pages/Account/MyRegistrations';
import CoachSignup from './pages/Auth/CoachSignup';
import StudentRegister from './pages/Auth/StudentRegister';
import './App.css';

function App() {
  return (
    <AuthProvider>
      <Router>
        <div className="App">
          <Routes>
            <Route path="/" element={<Welcome />} />
            <Route
              path="/home"
              element={
                <ProtectedRoute>
                  <>
                    <Navbar />
                    <Home />
                  </>
                </ProtectedRoute>
              }
            />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Register />} />
            <Route path="/student/register" element={<StudentRegister />} />
            <Route path="/coach/signup" element={<CoachSignup />} />
            <Route
              element={
                <ProtectedRoute>
                  <>
                    <Navbar />
                    <main className="main-content">
                      <Routes>
                        <Route path="/live-events" element={<LiveEvents />} />
                        <Route path="/player-stats" element={<PlayerStats />} />
                        <Route path="/departments" element={<Departments />} />
                        <Route path="/tournaments" element={<Tournaments />} />
                        <Route path="/account/profile" element={<Profile />} />
                        <Route path="/account/settings" element={<Settings />} />
                        <Route path="/account/registrations" element={<MyRegistrations />} />
                      </Routes>
                    </main>
                  </>
                </ProtectedRoute>
              }
            />
          </Routes>
          <Chatbot />
        </div>
      </Router>
    </AuthProvider>
  );
}

export default App;
