import React, { useState } from 'react';
import Card from '../../components/Card/Card';
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';
import { useAuth } from '../../context/AuthContext';
import './Account.css';
import PasswordModal from '../../components/Modal/PasswordModal';
import ConfirmModal from '../../components/Modal/ConfirmModal';

const Settings = () => {
  const { user, logout, navigate } = useAuth();
  const [formData, setFormData] = useState({
    name: user?.name || '',
    email: user?.email || '',
    department: user?.department || '',
    phone: user?.phone || '',
    notifications: true,
    darkMode: false,
    language: 'english'
  });

  const [message, setMessage] = useState({ text: '', type: '' });
  const [showPasswordModal, setShowPasswordModal] = useState(false);
  const [showDeactivateModal, setShowDeactivateModal] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically update the user settings in your backend
    setMessage({ text: 'Settings updated successfully!', type: 'success' });
    setTimeout(() => setMessage({ text: '', type: '' }), 3000);
  };

  const handlePasswordChange = (passwords) => {
    // Here you would typically make an API call to change the password
    console.log('Changing password:', passwords);
    setMessage({ text: 'Password changed successfully!', type: 'success' });
    setShowPasswordModal(false);
  };

  const handleDeactivateAccount = () => {
    // Here you would typically make an API call to deactivate the account
    console.log('Deactivating account');
    logout(); // Use the logout function from useAuth
    navigate('/'); // Redirect to home page
  };

  return (
    <div className="account-container">
      <h1>Account Settings</h1>
      
      <div className="settings-grid">
        <Card>
          <div className="settings-section">
            <h2>Profile Settings</h2>
            <form onSubmit={handleSubmit} className="settings-form">
              <Input
                label="Full Name"
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
              />
              <Input
                label="Email"
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
              />
              <Input
                label="Department"
                type="text"
                name="department"
                value={formData.department}
                onChange={handleChange}
              />
              <Input
                label="Phone Number"
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
              />
              <Button type="submit" variant="primary">
                Save Profile Changes
              </Button>
            </form>
          </div>
        </Card>

        <Card>
          <div className="settings-section">
            <h2>Preferences</h2>
            <div className="preferences-form">
              <div className="preference-item">
                <label className="toggle-label">
                  <span>Email Notifications</span>
                  <input
                    type="checkbox"
                    name="notifications"
                    checked={formData.notifications}
                    onChange={handleChange}
                    className="toggle-input"
                  />
                  <span className="toggle-slider"></span>
                </label>
              </div>
              <div className="preference-item">
                <label className="toggle-label">
                  <span>Dark Mode</span>
                  <input
                    type="checkbox"
                    name="darkMode"
                    checked={formData.darkMode}
                    onChange={handleChange}
                    className="toggle-input"
                  />
                  <span className="toggle-slider"></span>
                </label>
              </div>
              <div className="preference-item">
                <label>Language</label>
                <select
                  name="language"
                  value={formData.language}
                  onChange={handleChange}
                  className="settings-select"
                >
                  <option value="english">English</option>
                  <option value="hindi">Hindi</option>
                  <option value="telugu">Telugu</option>
                </select>
              </div>
            </div>
          </div>
        </Card>

        <Card>
          <div className="settings-section danger-zone">
            <h2>Account Management</h2>
            <div className="danger-zone-actions">
              <Button 
                variant="secondary"
                onClick={() => setShowPasswordModal(true)}
              >
                Change Password
              </Button>
              <Button 
                variant="secondary"
                onClick={() => setShowDeactivateModal(true)}
                className="danger"
              >
                Deactivate Account
              </Button>
            </div>
          </div>
        </Card>
      </div>

      {message.text && (
        <div className={`settings-message ${message.type}`}>
          {message.text}
        </div>
      )}

      {showPasswordModal && (
        <PasswordModal
          onClose={() => setShowPasswordModal(false)}
          onSubmit={handlePasswordChange}
        />
      )}
      
      {showDeactivateModal && (
        <ConfirmModal
          title="Deactivate Account"
          message="Are you sure you want to deactivate your account? This action cannot be undone."
          onConfirm={handleDeactivateAccount}
          onClose={() => setShowDeactivateModal(false)}
        />
      )}
    </div>
  );
};

export default Settings;
