import React, { useState } from 'react';
import './Events.css';

const Events = () => {
  const [eventData, setEventData] = useState({
    title: '',
    description: '',
    location: '',
    datetime: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement event creation logic
    console.log(eventData);
  };

  return (
    <div className="events-container">
      <h1 className="page-title">Create New Event</h1>

      <div className="event-form-card">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="title">Event Title</label>
            <input
              id="title"
              type="text"
              value={eventData.title}
              onChange={(e) => setEventData({ ...eventData, title: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="description">Event Description</label>
            <textarea
              id="description"
              rows="4"
              value={eventData.description}
              onChange={(e) => setEventData({ ...eventData, description: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="location">Location</label>
            <input
              id="location"
              type="text"
              value={eventData.location}
              onChange={(e) => setEventData({ ...eventData, location: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="datetime">Event Date & Time</label>
            <input
              id="datetime"
              type="datetime-local"
              value={eventData.datetime}
              onChange={(e) => setEventData({ ...eventData, datetime: e.target.value })}
              required
            />
          </div>

          <div className="form-actions">
            <button type="submit" className="submit-button">
              Create Event
            </button>
          </div>
        </form>
      </div>

      <h2 className="section-title">Upcoming Events</h2>

      <div className="events-grid">
        {/* Sample Event Card */}
        <div className="event-card">
          <h3>Sample Event</h3>
          <p className="event-detail">Location: Sample Location</p>
          <p className="event-detail">Date: Jan 1, 2024</p>
          <p className="event-description">
            This is a sample event description. Replace this with real events from your database.
          </p>
        </div>
      </div>
    </div>
  );
};

export default Events;
