import React from 'react';
import Card from '../../components/Card/Card';
import { useAuth } from '../../context/AuthContext';
import './Account.css';

const MyRegistrations = () => {
  const { user } = useAuth();
  const registrations = [
    {
      id: 1,
      event: "Inter-Department Basketball Championship",
      date: "2024-03-20",
      status: "Confirmed",
      venue: "Main Stadium",
      time: "16:00",
      team: "CSE Team"
    },
    {
      id: 2,
      event: "Cricket Tournament",
      date: "2024-03-28",
      status: "Pending",
      venue: "Cricket Ground",
      time: "14:30",
      team: "Department Team"
    },
    {
      id: 3,
      event: "Table Tennis Tournament",
      date: "2024-04-02",
      status: "Confirmed",
      venue: "Sports Hall",
      time: "10:00",
      category: "Singles"
    }
  ];

  return (
    <div className="account-container">
      <h1>My Registrations</h1>
      
      <div className="registrations-grid">
        {registrations.map(reg => (
          <Card key={reg.id}>
            <div className="registration-card">
              <div className="registration-header">
                <h3>{reg.event}</h3>
                <span className={`status ${reg.status.toLowerCase()}`}>
                  {reg.status}
                </span>
              </div>
              
              <div className="registration-details">
                <div className="detail-item">
                  <span className="detail-label">Date:</span>
                  <span>{reg.date}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Time:</span>
                  <span>{reg.time}</span>
                </div>
                <div className="detail-item">
                  <span className="detail-label">Venue:</span>
                  <span>{reg.venue}</span>
                </div>
                {reg.team && (
                  <div className="detail-item">
                    <span className="detail-label">Team:</span>
                    <span>{reg.team}</span>
                  </div>
                )}
                {reg.category && (
                  <div className="detail-item">
                    <span className="detail-label">Category:</span>
                    <span>{reg.category}</span>
                  </div>
                )}
              </div>

              <div className="registration-actions">
                <button className="action-button view">
                  View Details
                </button>
                {reg.status === 'Pending' && (
                  <button className="action-button cancel">
                    Cancel Registration
                  </button>
                )}
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default MyRegistrations;
