import React from 'react';
import './LiveMatches.css';

const LiveMatches = () => {
  const liveMatches = [
    {
      id: 1,
      sport: 'Cricket',
      teams: {
        team1: { name: 'Team A', score: '156/4' },
        team2: { name: 'Team B', score: '142/6' }
      },
      status: 'Live',
      overs: '18.4/20',
      venue: 'Main Ground'
    },
    {
      id: 2,
      sport: 'Basketball',
      teams: {
        team1: { name: 'Dragons', score: '78' },
        team2: { name: 'Phoenix', score: '82' }
      },
      status: 'Live',
      time: 'Q4 - 2:45',
      venue: 'Indoor Court 1'
    },
    {
      id: 3,
      sport: 'Football',
      teams: {
        team1: { name: 'United', score: '2' },
        team2: { name: 'City', score: '1' }
      },
      status: 'Live',
      time: '75\'',
      venue: 'Football Ground'
    }
  ];

  return (
    <div className="live-matches-container">
      <h1 className="section-title">Live Matches</h1>
      
      <div className="matches-grid">
        {liveMatches.map(match => (
          <div key={match.id} className="match-card">
            <div className="match-header">
              <span className="sport-tag">{match.sport}</span>
              <span className="live-indicator">LIVE</span>
            </div>

            <div className="match-content">
              <div className="team team1">
                <h3>{match.teams.team1.name}</h3>
                <span className="score">{match.teams.team1.score}</span>
              </div>

              <div className="match-info">
                <span className="vs">VS</span>
                <span className="match-time">
                  {match.overs || match.time}
                </span>
              </div>

              <div className="team team2">
                <h3>{match.teams.team2.name}</h3>
                <span className="score">{match.teams.team2.score}</span>
              </div>
            </div>

            <div className="match-footer">
              <span className="venue">
                <i className="fas fa-map-marker-alt"></i> {match.venue}
              </span>
              <button className="btn-watch">Watch Live</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default LiveMatches;
