import React, { useState } from 'react';
import Card from '../../components/Card/Card';
import './PlayerStats.css';

const PlayerStats = () => {
  const [players] = useState([
    {
      id: 1,
      name: "John Doe",
      team: "Team A",
      position: "Forward",
      stats: {
        matches: 25,
        goals: 15,
        assists: 8,
        rating: 8.5
      }
    },
    {
      id: 2,
      name: "Jane Smith",
      team: "Team B",
      position: "Midfielder",
      stats: {
        matches: 23,
        goals: 7,
        assists: 12,
        rating: 8.2
      }
    }
  ]);

  return (
    <div className="player-stats-container">
      <h1>Player Statistics</h1>
      <div className="stats-grid">
        {players.map(player => (
          <Card key={player.id}>
            <div className="player-card">
              <div className="player-info">
                <h3>{player.name}</h3>
                <p>{player.team} - {player.position}</p>
              </div>
              <div className="stats-grid-container">
                <div className="stat-item">
                  <span className="stat-label">Matches</span>
                  <span className="stat-value">{player.stats.matches}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Goals</span>
                  <span className="stat-value">{player.stats.goals}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Assists</span>
                  <span className="stat-value">{player.stats.assists}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">Rating</span>
                  <span className="stat-value">{player.stats.rating}</span>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default PlayerStats;
