import React from 'react';
import Card from '../../components/Card/Card';
import './Home.css';

const Home = () => {
  const featuredEvents = [
    {
      id: 1,
      title: "Inter-Department Basketball Championship",
      date: "2024-03-20",
      time: "16:00",
      teams: "Engineering vs Medical",
      venue: "Main Stadium"
    },
    {
      id: 2,
      title: "Annual Athletics Meet",
      date: "2024-03-25",
      time: "09:00",
      teams: "All Departments",
      venue: "Sports Complex"
    },
    {
      id: 3,
      title: "Cricket Tournament Finals",
      date: "2024-03-28",
      time: "14:30",
      teams: "Science vs Arts",
      venue: "Cricket Ground"
    },
    {
      id: 4,
      title: "Badminton Doubles Championship",
      date: "2024-03-22",
      time: "10:00",
      teams: "Department Pairs",
      venue: "Indoor Courts"
    },
    {
      id: 5,
      title: "Table Tennis Tournament",
      date: "2024-03-23",
      time: "11:00",
      teams: "Individual & Teams",
      venue: "Sports Hall"
    },
    {
      id: 6,
      title: "Swimming Championship",
      date: "2024-03-26",
      time: "09:30",
      teams: "All Categories",
      venue: "Swimming Pool"
    }
  ];

  const popularSports = [
    "Basketball", "Cricket", "Football", "Volleyball",
    "Athletics", "Badminton", "Table Tennis", "Swimming",
    "Chess", "Carrom", "Kabaddi", "Tennis"
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>KL Sports Hub</h1>
        <p>Empowering athletes, fostering competition, building champions</p>
      </div>

      <section className="popular-sports">
        <h2>Popular Sports</h2>
        <div className="sports-tags">
          {popularSports.map((sport, index) => (
            <span key={index} className="sport-tag">{sport}</span>
          ))}
        </div>
      </section>

      <section className="featured-events">
        <h2>Upcoming Events</h2>
        <div className="events-grid">
          {featuredEvents.map(event => (
            <Card key={event.id}>
              <div className="event-card">
                <h3>{event.title}</h3>
                <p className="event-teams">{event.teams}</p>
                <div className="event-details">
                  <span>{event.date}</span>
                  <span>{event.time}</span>
                </div>
                <p className="event-venue">{event.venue}</p>
              </div>
            </Card>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
