import React, { useState } from 'react';
import Card from '../../components/Card/Card';
import './Home.css';

const Home = () => {
  const [imageErrors, setImageErrors] = useState({});

  const handleImageError = (sportName) => {
    setImageErrors(prev => ({
      ...prev,
      [sportName]: true
    }));
  };

  const featuredEvents = [
    {
      id: 1,
      title: "Inter-Department Basketball Championship",
      date: "2024-03-20",
      time: "16:00",
      teams: "Engineering vs Medical",
      venue: "Main Stadium"
    },
    {
      id: 2,
      title: "Annual Athletics Meet",
      date: "2024-03-25",
      time: "09:00",
      teams: "All Departments",
      venue: "Sports Complex"
    },
    {
      id: 3,
      title: "Cricket Tournament Finals",
      date: "2024-03-28",
      time: "14:30",
      teams: "Science vs Arts",
      venue: "Cricket Ground"
    },
    {
      id: 4,
      title: "Badminton Doubles Championship",
      date: "2024-03-22",
      time: "10:00",
      teams: "Department Pairs",
      venue: "Indoor Courts"
    },
    {
      id: 5,
      title: "Table Tennis Tournament",
      date: "2024-03-23",
      time: "11:00",
      teams: "Individual & Teams",
      venue: "Sports Hall"
    },
    {
      id: 6,
      title: "Swimming Championship",
      date: "2024-03-26",
      time: "09:30",
      teams: "All Categories",
      venue: "Swimming Pool"
    }
  ];

  const popularSports = [
    { 
      name: "Basketball", 
      image: "/images/sports/basketball.jpg",
      fallbackColor: "#ff4444"
    },
    { 
      name: "Cricket", 
      image: "/images/sports/cricket.jpg",
      fallbackColor: "#33b5e5"
    },
    { 
      name: "Football", 
      image: "/images/sports/football.jpg",
      fallbackColor: "#00C851"
    },
    { 
      name: "Volleyball", 
      image: "/images/sports/volleyball.jpg",
      fallbackColor: "#FF8800"
    },
    { 
      name: "Athletics", 
      image: "/images/sports/athletics.jpg",
      fallbackColor: "#2BBBAD"
    },
    { 
      name: "Badminton", 
      image: "/images/sports/badminton.jpg",
      fallbackColor: "#4285F4"
    },
    { 
      name: "Table Tennis", 
      image: "/images/sports/table-tennis.jpg",
      fallbackColor: "#aa66cc"
    },
    { 
      name: "Swimming", 
      image: "/images/sports/swimming.jpg",
      fallbackColor: "#00695c"
    },
    { 
      name: "Chess", 
      image: "/images/sports/chess.jpg",
      fallbackColor: "#3F729B"
    },
    { 
      name: "Carrom", 
      image: "/images/sports/carrom.jpg",
      fallbackColor: "#ff4444"
    },
    { 
      name: "Kabaddi", 
      image: "/images/sports/kabaddi.jpg",
      fallbackColor: "#1de9b6"
    },
    { 
      name: "Tennis", 
      image: "/images/sports/tennis.jpg",
      fallbackColor: "#fc6"
    }
  ];

  return (
    <div className="home-container">
      <div className="hero-section">
        <h1>KL Sports Hub</h1>
        <p>Empowering athletes, fostering competition, building champions</p>
      </div>

      <section className="popular-sports">
        <h2>Popular Sports</h2>
        <div className="sports-grid">
          {popularSports.map((sport, index) => (
            <div key={index} className="sport-card">
              <div 
                className="sport-image-container"
                style={imageErrors[sport.name] ? { 
                  backgroundColor: sport.fallbackColor,
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontSize: '2em'
                } : {}}
              >
                {!imageErrors[sport.name] ? (
                  <img 
                    src={process.env.PUBLIC_URL + sport.image} 
                    alt={sport.name} 
                    className="sport-image"
                    onError={() => handleImageError(sport.name)}
                  />
                ) : (
                  <span style={{ color: 'white' }}>
                    {sport.name.charAt(0)}
                  </span>
                )}
              </div>
              <span className="sport-name">{sport.name}</span>
            </div>
          ))}
        </div>
      </section>

      <section className="featured-events">
        <h2>Upcoming Events</h2>
        <div className="events-grid">
          {featuredEvents.map(event => (
            <Card key={event.id}>
              <div className="event-card">
                <h3>{event.title}</h3>
                <p className="event-teams">{event.teams}</p>
                <div className="event-details">
                  <span>{event.date}</span>
                  <span>{event.time}</span>
                </div>
                <p className="event-venue">{event.venue}</p>
              </div>
            </Card>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
