import React, { useState } from 'react';
import Card from '../../components/Card/Card';
import './LiveEvents.css';

const LiveEvents = () => {
  const [liveEvents] = useState([
    {
      id: 1,
      sport: "Basketball",
      teams: "Engineering vs Medical",
      score: "45 - 42",
      time: "Q3 5:30",
      venue: "Main Stadium",
      status: "Live"
    },
    {
      id: 2,
      sport: "Football",
      teams: "Arts vs Commerce",
      score: "2 - 1",
      time: "75'",
      venue: "Sports Complex",
      status: "Live"
    },
    {
      id: 3,
      sport: "Volleyball",
      teams: "Science vs Law",
      score: "2 - 1",
      time: "Set 3",
      venue: "Indoor Court",
      status: "Live"
    }
  ]);

  return (
    <div className="live-events-container">
      <h1>Live University Matches</h1>
      <div className="events-list">
        {liveEvents.map(event => (
          <Card key={event.id}>
            <div className="live-event-card">
              <div className="event-header">
                <span className="sport-type">{event.sport}</span>
                <span className="live-indicator">‚óè LIVE</span>
              </div>
              <div className="event-content">
                <h3>{event.teams}</h3>
                <div className="score-time">
                  <span className="score">{event.score}</span>
                  <span className="time">{event.time}</span>
                </div>
                <p className="event-venue">{event.venue}</p>
              </div>
              <button className="watch-button">Watch Live Stream</button>
            </div>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default LiveEvents;
