import React from 'react';
import './Home.css';
import basketballImg from '../../assets/sports/badminton.webp';
import cricketImg from '../../assets/sports/cricket.webp';
import footballImg from '../../assets/sports/football.webp';
import volleyballImg from '../../assets/sports/volleyball.jpg';
import athleticsImg from '../../assets/sports/athletics.jpg';
import badmintonImg from '../../assets/sports/badminton.jpg';

const sportsData = [
  {
    name: 'BASKETBALL',
    image: basketballImg,
    description: 'Develops teamwork, agility, and strategic thinking skills.'
  },
  {
    name: 'CRICKET',
    image: cricketImg,
    description: 'Enhances concentration, physical endurance, and decision-making skills. Perfect blend of individual skill and team strategy.'
  },
  {
    name: 'FOOTBALL',
    image: footballImg,
    description: 'Builds endurance, teamwork, and tactical understanding.'
  },
  {
    name: 'VOLLEYBALL',
    image: volleyballImg,
    description: 'Improves reflexes, coordination, and team communication.'
  },
  {
    name: 'ATHLETICS',
    image: athleticsImg,
    description: 'Develops speed, strength, and personal excellence.'
  },
  {
    name: 'BADMINTON',
    image: badmintonImg,
    description: 'Enhances agility, hand-eye coordination, and quick thinking.'
  }
];

const Home = () => {
  return (
    <div className="home-container">
      <h1 className="section-title">Popular Sports</h1>
      <div className="sports-grid">
        {sportsData.map((sport, index) => (
          <div key={index} className="sport-card">
            <div className="sport-image-container">
              <img src={sport.image} alt={sport.name} className="sport-image" />
            </div>
            <div className="sport-content">
              <h2>{sport.name}</h2>
              <p>{sport.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
