import React, { useState } from 'react';
import {
  Container,
  Box,
  Card,
  Grid,
  TextField,
  Button,
  Typography
} from '@mui/material';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';

const Events = () => {
  const [eventData, setEventData] = useState({
    title: '',
    description: '',
    location: '',
    datetime: new Date(),
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    // TODO: Implement event creation logic
    console.log(eventData);
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Typography variant="h4" gutterBottom component="h1">
        Create New Event
      </Typography>

      <Card sx={{ p: 3, mb: 4 }}>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Event Title"
                value={eventData.title}
                onChange={(e) => setEventData({ ...eventData, title: e.target.value })}
                required
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                multiline
                rows={4}
                label="Event Description"
                value={eventData.description}
                onChange={(e) => setEventData({ ...eventData, description: e.target.value })}
                required
              />
            </Grid>

            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Location"
                value={eventData.location}
                onChange={(e) => setEventData({ ...eventData, location: e.target.value })}
                required
              />
            </Grid>

            <Grid item xs={12}>
              <LocalizationProvider dateAdapter={AdapterDayjs}>
                <DateTimePicker
                  label="Event Date & Time"
                  value={eventData.datetime}
                  onChange={(newValue) => setEventData({ ...eventData, datetime: newValue })}
                  slotProps={{ textField: { fullWidth: true } }}
                />
              </LocalizationProvider>
            </Grid>

            <Grid item xs={12}>
              <Box display="flex" justifyContent="flex-end">
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  size="large"
                >
                  Create Event
                </Button>
              </Box>
            </Grid>
          </Grid>
        </form>
      </Card>

      <Typography variant="h4" gutterBottom>
        Upcoming Events
      </Typography>

      <Grid container spacing={3}>
        {/* Sample Event Card */}
        <Grid item xs={12} md={6} lg={4}>
          <Card sx={{ p: 2 }}>
            <Typography variant="h6">Sample Event</Typography>
            <Typography color="textSecondary">Location: Sample Location</Typography>
            <Typography color="textSecondary">Date: Jan 1, 2024</Typography>
            <Typography variant="body2" sx={{ mt: 2 }}>
              This is a sample event description. Replace this with real events from your database.
            </Typography>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Events;
