import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Card from '../../components/Card/Card';
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';
import './Auth.css';

const Register = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const totalSteps = 4;

  const sports = [
    "Basketball",
    "Cricket",
    "Football",
    "Volleyball",
    "Athletics",
    "Badminton",
    "Table Tennis",
    "Swimming",
    "Chess",
    "Carrom",
    "Kabaddi",
    "Tennis"
  ];

  const roleDescriptions = {
    student: "Join as a student to access sports training, book sessions, and track your progress",
    coach: "Join as a coach to offer your expertise and connect with students"
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.role === 'coach') {
      navigate('/coach/signup', { state: { sport: formData.sport } });
    } else {
      console.log('Register data:', formData);
    }
  };

  return (
    <div className="auth-container">
      <Card>
        <div className="progress-bar">
          {[...Array(totalSteps)].map((_, index) => (
            <div 
              key={index}
              className={`progress-step ${
                currentStep > index + 1 
                  ? 'completed' 
                  : currentStep === index + 1 
                  ? 'active' 
                  : ''
              }`}
            >
              {index + 1}
            </div>
          ))}
        </div>

        <div className={`form-step ${currentStep === 1 ? 'active' : ''}`}>
          <div className="step-content">
            <h2 className="step-title">Choose Your Role</h2>
            <p className="step-description">Are you joining as a student or coach?</p>
          </div>
          <div className="role-selector">
            <h3>I want to join as:</h3>
            <div className="role-options">
              <label className={`role-option ${formData.role === 'student' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="role"
                  value="student"
                  checked={formData.role === 'student'}
                  onChange={handleChange}
                />
                <span>Student</span>
              </label>
              <label className={`role-option ${formData.role === 'coach' ? 'selected' : ''}`}>
                <input
                  type="radio"
                  name="role"
                  value="coach"
                  checked={formData.role === 'coach'}
                  onChange={handleChange}
                />
                <span>Coach</span>
              </label>
            </div>
            <p className="role-description">
              {roleDescriptions[formData.role]}
            </p>
          </div>

          {formData.role === 'coach' && (
            <div className="sport-selector">
              <label>Select Sport:</label>
              <select
                name="sport"
                value={formData.sport}
                onChange={handleChange}
                className="auth-select"
                required
              >
                <option value="">Choose a sport</option>
                {sports.map((sport, index) => (
                  <option key={index} value={sport}>{sport}</option>
                ))}
              </select>
            </div>
          )}

          <div className="form-nav">
            {currentStep > 1 && (
              <button 
                className="nav-button prev-button"
                onClick={() => setCurrentStep(prev => prev - 1)}
              >
                Previous
              </button>
            )}
            {currentStep < totalSteps && (
              <button 
                className="nav-button next-button"
                onClick={() => setCurrentStep(prev => prev + 1)}
              >
                Next
              </button>
            )}
          </div>
        </div>

        <div className={`form-step ${currentStep === 2 ? 'active' : ''}`}>
          <div className="step-content">
            <h2 className="step-title">Basic Information</h2>
            <p className="step-description">Tell us a bit about yourself</p>
          </div>
          {formData.role === 'student' ? (
            <>
              <Input
                label="Full Name"
                type="text"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <Input
                label="Email"
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <Input
                label="Password"
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              <Input
                label="Confirm Password"
                type="password"
                name="confirmPassword"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
              <Button type="submit" variant="primary">
                Register
              </Button>
            </>
          ) : (
            <div className="coach-signup-prompt">
              {formData.sport ? (
                <>
                  <p>Register as {formData.sport} Coach</p>
                  <p className="coach-requirements">Requirements:</p>
                  <ul className="requirements-list">
                    <li>Minimum 2 years of coaching experience</li>
                    <li>Valid coaching certification</li>
                    <li>Background in {formData.sport}</li>
                  </ul>
                </>
              ) : (
                <p>Please select a sport to continue registration</p>
              )}
              <Button 
                type="submit" 
                variant="primary"
                disabled={!formData.sport}
              >
                Continue to Coach Registration
              </Button>
            </div>
          )}

          <div className="auth-links">
            <Link to="/login">Already have an account? Login</Link>
          </div>
        </div>
      </Card>
    </div>
  );
};

export default Register;
