import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isAccountMenuOpen, setIsAccountMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Set to true temporarily for testing

  const handleLogout = () => {
    setIsLoggedIn(false);
    // Add your logout logic here
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-brand">
          KL SportsHub
        </Link>
        <div className="navbar-links">
          <Link to="/live-events">Live Matches</Link>
          <Link to="/tournaments">Tournaments</Link>
          <Link to="/player-stats">Athletes</Link>
          <Link to="/departments">Departments</Link>
          
          {!isLoggedIn ? (
            <>
              <Link to="/login" className="nav-button">Login</Link>
              <Link to="/register" className="nav-button primary">Register</Link>
            </>
          ) : (
            <div className="account-dropdown">
              <button 
                className="account-button"
                onClick={() => setIsAccountMenuOpen(!isAccountMenuOpen)}
              >
                My Account
              </button>
              {isAccountMenuOpen && (
                <div className="account-menu">
                  <Link to="/account/profile">My Profile</Link>
                  <Link to="/account/registrations">My Registrations</Link>
                  <Link to="/account/settings">Settings</Link>
                  <button onClick={handleLogout} className="logout-button">
                    Logout
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
