import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import Card from '../../components/Card/Card';
import Input from '../../components/Input/Input';
import Button from '../../components/Button/Button';
import './Account.css';

const Profile = () => {
  const { user, login } = useAuth();
  const [isEditing, setIsEditing] = useState(false);
  const [formData, setFormData] = useState({
    name: user?.name || 'User',
    email: user?.email || '123456@gmail.com',
    department: user?.department || 'Computer Science',
    role: user?.role || 'Student Athlete',
    phone: user?.phone || '',
    sports: user?.sports || ["Basketball", "Cricket"],
    registrations: [
      {
        id: 1,
        event: "Inter-Department Basketball Championship",
        date: "2024-03-20",
        status: "Confirmed"
      },
      {
        id: 2,
        event: "Cricket Tournament",
        date: "2024-03-28",
        status: "Pending"
      }
    ]
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Update user info in auth context
    login({
      ...user,
      ...formData
    });
    setIsEditing(false);
  };

  return (
    <div className="account-container">
      <h1>My Profile</h1>
      
      <div className="profile-grid">
        <Card>
          <div className="profile-info">
            <div className="profile-header">
              <h2>Personal Information</h2>
              {!isEditing && (
                <Button 
                  variant="secondary" 
                  onClick={() => setIsEditing(true)}
                  className="edit-button"
                >
                  Edit Profile
                </Button>
              )}
            </div>
            
            {isEditing ? (
              <form onSubmit={handleSubmit} className="profile-form">
                <Input
                  label="Full Name"
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                />
                <Input
                  label="Email"
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                />
                <Input
                  label="Department"
                  type="text"
                  name="department"
                  value={formData.department}
                  onChange={handleChange}
                />
                <Input
                  label="Phone Number"
                  type="tel"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                />
                <div className="form-buttons">
                  <Button type="submit" variant="primary">
                    Save Changes
                  </Button>
                  <Button 
                    type="button" 
                    variant="secondary"
                    onClick={() => setIsEditing(false)}
                  >
                    Cancel
                  </Button>
                </div>
              </form>
            ) : (
              <>
                <div className="info-group">
                  <label>Name:</label>
                  <span>{formData.name}</span>
                </div>
                <div className="info-group">
                  <label>Email:</label>
                  <span>{formData.email}</span>
                </div>
                <div className="info-group">
                  <label>Department:</label>
                  <span>{formData.department}</span>
                </div>
                <div className="info-group">
                  <label>Role:</label>
                  <span>{formData.role}</span>
                </div>
                {formData.phone && (
                  <div className="info-group">
                    <label>Phone:</label>
                    <span>{formData.phone}</span>
                  </div>
                )}
                <div className="info-group">
                  <label>Sports:</label>
                  <div className="sports-tags">
                    {formData.sports.map((sport, index) => (
                      <span key={index} className="sport-tag">{sport}</span>
                    ))}
                  </div>
                </div>
              </>
            )}
          </div>
        </Card>

        <Card>
          <div className="registrations-list">
            <h2>My Registrations</h2>
            {formData.registrations.map(reg => (
              <div key={reg.id} className="registration-item">
                <h3>{reg.event}</h3>
                <p>Date: {reg.date}</p>
                <span className={`status ${reg.status.toLowerCase()}`}>
                  {reg.status}
                </span>
              </div>
            ))}
          </div>
        </Card>
      </div>
    </div>
  );
};

export default Profile;
